INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31816, 'Cartizad 30 tab', 'كارتيزاد 30 قرص', '300', '195', 'Anti-rheumatic.osteoarthritis.anabolic agents', 'Glucosamine hydrochloride+chondroitin sulfate+hyaluronic+msm acid+calcium fructoborate+maltodextrin', 'Pharma zad > safe life', 'Tab', 'أقراص', NULL, '3', 'لإعادة بناء الغضاريف وتقليل الالتهابات الناتجة عن التآكل وتخفيف الالم بسبب التهابات الروماتيزم والعظام والمفاصل', 'for joints health and cartilages support', NULL, NULL, 25212, '2025-10-23', 0, 'Glucosamine is generally used over the counter in the symptomatic treatment of osteoarthritis and joint pain,[A2352] frequently combined with chondroitin sulfate and/or ibuprofen.[L32699]', 'The mechanism of action of glucosamine in joint health is unclear,[A231894] however there are several possible mechanisms that contribute to its therapeutic effects. Because glucosamine is a precursor for glycosaminoglycans, and glycosaminoglycans are a major component of joint cartilage,[A232289] glucosamine supplements may help to rebuild cartilage and treat the symptoms of arthritis.  Some in vitro studies show evidence that glucosamine reduces inflammation via inhibition of interferon gamma[A12619,A12616,A12621]  and Nuclear factor kappa B subunit 65 (NF-κB p65),[A12616,A232284] improving the symptoms of arthritis and joint pain. Clinical relevance is unknown at this time.', 'The administration of glucosamine, in theory, provides a building block towards the synthesis of glycosaminoglycans, slowing the progression of osteoarthritis and relieving symptoms of joint pain. Studies to this date examining the efficacy of glucosamine sulfate have been inconclusive. Glycosaminoglycans contribute to joint cartilage elasticity, strength, and flexibility.[A232144] A systematic review of various studies and guidelines determined that modest improvements were reported for joint pain and function in patients taking glucosamine. A consistent joint space narrowing was observed, but with an unclear clinical significance.[A231894]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31817, 'Becalocyrl 50 mg 28 f.c. tabs.', 'بيكالوكيرل 50مجم 28 قرص', '611', '432', 'Antineoplastic', 'Bicalutamide', 'Marcyrl pharmaceutical industries (mpi)', 'Tab', 'أقراص', '50 mg', '4', 'يستخدم لعلاج سرطان البروستاتا', 'about bicalutamide non steroidal anti estrogen anti neoplastic. mechanism of action of bicalutamide this antiandrogenic drug binds to the receptor of androgens on the cells of prostate gland. thus it blocks the action of androgen on prostate gland and pre', NULL, NULL, 1846, '2025-08-11', 0, 'Bicalutamide is indicated in combination with a luteinizing hormone-releasing hormone (LHRH) agonist for the treatment of Stage D2 metastatic carcinoma of the prostate.[L40478]', 'Bicalutamide competes with androgen for the binding of androgen receptors, consequently blocking the action of androgens of adrenal and testicular origin which stimulate the growth of normal and malignant prostatic tissue.', 'Bicalutamide is an antineoplastic hormonal agent primarily used in the treatment of prostate cancer. Bicalutamide is a pure, nonsteroidal anti-androgen with affinity for androgen receptors (but not for progestogen, estrogen, or glucocorticoid receptors). Consequently, Bicalutamide blocks the action of androgens of adrenal and testicular origin which stimulate the growth of normal and malignant prostatic tissue. Prostate cancer is mostly androgen-dependent and can be treated with surgical or chemical castration. To date, antiandrogen monotherapy has not consistently been shown to be equivalent to castration.', 'DrugBank', 0, 0, 0);
