INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (28154, 'Thrombophob-s 30000 i.u./100gm gel (n/a)', 'ثرومبوفوب -اس 30000وحده100جم جل', '2', NULL, 'Anticoagulant.high mwt heparin', 'Heparin', 'Alexandria > abbott laboratories', 'Gel', 'جل', '100gm', '1', NULL, 'about heparin a highly-sulfated glycosaminoglycan anticoagulant. mechanism of action of heparin the anticoagulant action of heparin is exerted through affecting conversion of fibrinogen to fibrin mediated by factors xa as well as iia. it acts by activatin', NULL, NULL, 937, '2022-07-07', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Unfractionated heparin (UH) is a heterogenous preparation of anionic, sulfated glycosaminoglycan polymers with weights ranging from 3000 to 30,000 Da. It is a naturally occurring anticoagulant released from mast cells. It binds reversibly to antithrombin III (ATIII) and greatly accelerates the rate at which ATIII inactivates coagulation enzymes thrombin (factor IIa) and factor Xa. UH is different from low molecular weight heparin (LMWH) in the following ways: the average molecular weight of LMWH is about 4.5 kDa whereas it is 15 kDa for UH; UH requires continuous infusions; activated partial prothrombin time (aPTT) monitoring is required when using UH; and UH has a higher risk of bleeding and higher risk of osteoporosis in long term use. Unfractionated heparin is more specific than LMWH for thrombin. Furthermore, the effects of UH can typically be reversed by using protamine sulfate.', '["B01AB51", "C05BA03", "S01XA14", "C05BA53", "B01AB01"]', '[]');
