INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32100, 'Regetal cream 30 gm', 'ريجيتال كريم 30 جم', '120', '85', 'Skin care.moisturizing', 'Panthenol+aloe vera+calendula oil+propolis+lavender oil+vitamin e+vitamin a', 'Aglan pharma', 'Cream', 'كريم', '30 gm', '1', NULL, NULL, NULL, NULL, 1066, '2025-04-11', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
