INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tricardia mr 35mg 30 f.c. tabs.', 28486, 'تريكارديا ام ار 35مجم 30 قرص', '23', NULL, 'Anti-ischemic', 'Anti-ischemic', NULL, 'Trimetazidine', 'Rameda', 'Tab', 'أقراص', '35mg', '3', NULL, NULL, 'about trimetazidine cytoprotective vasodilator anti-ischemic (anti-anginal) agent. mechanism of action of trimetazidine it is a cytoprotective drug which reduces anginal attacks and improves exercise tolerance by nonhaemodynamic mechanisms. it improves ce', NULL, 1216, '2022-08-31', NULL, 0, 'Trimetazidine is indicated for the symptomatic treatment of stable angina pectoris in patients inadequately controlled or intolerant to first line therapies.[L33015]', 'During myocardial ischemia, anaerobic metabolism takes over, increasing levels of lactic acid.[A233215] The decreased intracellular pH and increased concentration of protons activates sodium-hydrogen and sodium-calcium antiport systems, raising intracellular calcium concentrations, finally leading to decreased contractility.[A233215]



This injury to the myocardium raises concentrations of catecholamines, which activate hormone sensitive lipase, and increasing fatty acid concentrations in plasma.[A233215] When the myocardium is repurfused, fatty acid oxidation becomes the dominant form of ATP production, maintaining an acidic pH, and further exacerbating the injury.[A233215]



The mechanism of action of trimetazidine is not fully understood.[A233215] Trimetazidine may inhibit mitochondrial 3-ketoacyl coenzyme A thiolase, decreasing long chain fatty acid β-oxidation but not glycolysis in the myocardium.[A7688,L33020] The decreased long chain fatty acid β-oxidation is compensated for by increased use of glucose, preventing a lowered myocardial pH, and further decreases in contractility.[A7688,L33020] However, another study suggests that 3-ketoacyl coenzyme A thiolase may not be trimetazidine''s target, and that this mechanism may be incorrect.[A233215]', 'Trimetazidine is indicated for the symptomatic treatment of stable angina pectoris in patients inadequately controlled or intolerant to first line therapies.[L33015] Patients should be counselled regarding the risk of use with reduced renal or hepatic function, worsening of extrapyramidal symptoms or other movement disorders, and risk of falls.[L33020]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Carboplatin ebewe 150 mg vial', 6969, 'كاربوبلاتين ابوي 150مجم فيال', '180', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Carboplatin', 'Novartis scientific office', 'Vial', 'فيال', '150 mg', '1', NULL, NULL, NULL, NULL, 824, '2025-08-16', NULL, 0, 'Carboplatin is indicated in combination with an established combination of chemotherapeutic agents for the initial treatment of advanced ovarian carcinoma.[L32253] Carboplatin is also indicated for the palliative treatment of ovarian carcinoma, recurrent after prior chemotherapy.[L32253]', 'Carboplatin predominantly acts by attaching alkyl groups to the nucleotides, leading to the formation of monoadducts, and DNA fragmenting when repair enzymes attempt to correct the error.[A230158,L32253] 2% of carboplatin''s activity comes from DNA cross-linking from a base on one strand to a base on another, preventing DNA strands from separating for synthesis or transcription.[A230158,A1472] Finally, carboplatin can induce a number of different mutations.[A230158]', 'Carboplatin is an organoplatinum antineoplastic alkylating agent used in the treatment of advanced ovarian carcinoma.[L32253] Carboplatin has a long duration of action as it is given every 4 weeks, and a narrow therapeutic index.[L32253] Patients should be counselled regarding bone marrow suppression and anemia.[L32253]', 'DrugBank');
