INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tecavir 0.05mg/ml oral soln. 210 ml', 27960, 'تيكافير 0.05مجم/مل محلول بالفم 210 مل', '450', NULL, 'Antiviral.nucleoside', 'Antiviral.nucleoside', NULL, 'Entecavir', 'Eva pharma', 'Sol', 'محلول', '0.05mg', '1', NULL, NULL, 'about entecavir nucleoside analog reverse transcriptase inhibitor oral antiviral drug used in the treatment of hepatitis b infection. mechanism of action of entecavir by competing with the natural substrate deoxyguanosine triphosphate entecavir functional', NULL, 881, '2022-06-17', NULL, 0, 'For the treatment of chronic hepatitis B virus infection in adults with evidence of active viral replication and either evidence of persistent elevations in serum aminotransferases (ALT or AST) or histologically active disease.', 'By competing with the natural substrate deoxyguanosine triphosphate, entecavir functionally inhibits all three activities of the HBV polymerase (reverse transcriptase, rt): (1) base priming, (2) reverse transcription of the negative strand from the pregenomic messenger RNA, and (3) synthesis of the positive strand of HBV DNA. Upon activation by kinases, the drug can be incorporated into the DNA which has the ultimate effect of inhibiting the HBV polymerase activity.', 'Entecavir is a guanosine nucleoside analogue with selective activity against hepatitis B virus (HBV). It is designed to selectively inhibit the Hepatitis B virus, blocking all three steps in the replication process. Entecavir is more efficient than an older Hepatitis B drug, lamivudine.', 'DrugBank');
