INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lomy massage cream 60 gm', 22007, 'لومي كريم مساج 60 جم', '35', NULL, 'Massage', 'Massage', NULL, 'Vitamin c+cucumber ext.+green tea oil+almond oil+tocopherol+eucalyptus oil+menthol+panthenol+glycerin+camphor oil', 'Hi-care > famila medifood', 'Cream', 'كريم', '60 gm', '1', NULL, NULL, NULL, NULL, 630, '2022-07-13', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Betagan 0.5% eye drops 5 ml', 1442, 'بيتاجان قطرة 5عين مل', '22', NULL, 'Antiglaucoma', 'Antiglaucoma', NULL, 'Levobunolol hydrochloride 0.5%', 'Allergan > soficopharm', 'Drops', 'نقط', '0.5%', '1', NULL, NULL, 'about levobunolol a non-selective beta blocker antiglaucoma(topical). mechanism of action of levobunolol levobunolol is a non selective beta blocker. it inhibits the beta receptors (?2) present in the eye such as in the ciliary body epithelium and blood v', '5016007203325', 1031, '2022-12-03', NULL, 0, 'For lowering intraocular pressure (IOP) and may be used in patients with chronic open-angle glaucoma or ocular hypertension.', 'Levobunolol''s mechanism of action in reducing IOP is not clearly defined, but is believed to be due to a reduction of the production of aqueous humor via blockage of endogenous catecholamine-stimulated increases in cyclic adenosine monophosphate (AMP) concentrations within the ciliary processes.', 'Levobunolol is an ophthalmic beta-blocker, equally effective at &beta;(1)- and &beta;(2)-receptor sites. Levobunolol reduces both elevated and normal IOP in patients with or without glaucoma. In patients with elevated IOP, levobunolol reduces mean IOP by approximately 25-40% from baseline. As the drug is a nonselective &beta-adrenergic blocking agent, it can produce both systemic pulmonary and cardiovascular effects following topical application to the eye. These effects include adverse pulmonary effects (eg. bronchoconstriction, increased airway resistance), and a decrease in blood pressure and heart rate. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('5-fluorouracil-ebewe 250mg/5ml i.v. vial', 3, 'فلورويوراسيل 250مجم/10مل 5 امبولات وريد', '74', '50', 'Antineoplastic', 'Antineoplastic', NULL, 'Fluorouracil', 'Shanghai xudong haipu pharmaceutical > acdima international trading', 'Vial', 'فيال', '250mg', '1', 'مضاد للاورام', NULL, 'description a pyrimidine analog that is an antineoplastic antimetabolite. it interferes with dna synthesis by blocking the thymidylate synthetase conversion of deoxyuridylic acid to thymidylic acid mechanism of action the precise mechanism of action has n', '9088881320685', 7024, '2025-01-18', NULL, 0, 'For the topical treatment of multiple actinic or solar keratoses. In the 5% strength it is also useful in the treatment of superficial basal cell carcinomas when conventional methods are impractical, such as with multiple lesions or difficult treatment sites. Fluorouracil injection is indicated in the palliative management of some types of cancer, including colon, esophageal, gastric, rectum, breast, biliary tract, stomach, head and neck, cervical, pancreas, renal cell, and carcinoid.', 'The precise mechanism of action has not been fully determined, but the main mechanism of fluorouracil is thought to be the binding of the deoxyribonucleotide of the drug (FdUMP) and the folate cofactor, N5–10-methylenetetrahydrofolate, to thymidylate synthase (TS) to form a covalently bound ternary complex. This results in the inhibition of the formation of thymidylate from uracil, which leads to the inhibition of DNA and RNA synthesis and cell death. Fluorouracil can also be incorporated into RNA in place of uridine triphosphate (UTP), producing a fraudulent RNA and interfering with RNA processing and protein synthesis.', 'Fluorouracil is an antineoplastic anti-metabolite. Anti-metabolites masquerade as purine or pyrimidine - which become the building blocks of DNA. They prevent these substances from becoming incorporated into DNA during the "S" phase (of the cell cycle), stopping normal development and division. Fluorouracil blocks an enzyme which converts the cytosine nucleotide into the deoxy derivative. In addition, DNA synthesis is further inhibited because Fluorouracil blocks the incorporation of the thymidine nucleotide into the DNA strand.', 'DrugBank');
