INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Urginafect 5mg 20 f.c. tablets', 13015, 'ارجينافكت 5مجم 20 قرص', '72', '53', 'Urinary incontinence - antispasmodics - muscarinic antagonists', 'Urinary incontinence - antispasmodics - muscarinic antagonists', NULL, 'Solifenacin succinate', 'UTOPIA PHARMACEUTICALS COMPANY', 'Tablet', 'أقراص', '5mg', '2', '1- علاج حالات التبول اللإرادي.', NULL, NULL, '6223012330055', 6808, '2025-06-27', NULL, 0, 'Solifenacin tablets are indicated to treat an overactive bladder with urinary incontinence, urgency, and frequency.[L7511]', 'Solifenacin is a competitive muscarinic receptor antagonist.[L7511] It has the highest affinity for M3, M1, and M2 muscarinic receptors.[A181247] 80% of the muscarinic receptors in the bladder are M2, while 20% are M3.[A181262] Solifenacin''s antagonism of the M3 receptor prevents contraction of the detrusor muscle, while antagonism of the M2 receptor may prevent contraction of smooth muscle in the bladder.[A181262]', 'Solifenacin antagonizes the M2 and M3 muscarinic receptors in the bladder to treat an overactive bladder.[L7511,A181262] It has a long duration of action as it is usually taken once daily.[L7511] Patients taking solifenacin should be aware of the risks of angioedema and anaphylaxis.[L7511]', 'DrugBank');
