INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22458, 'Mefronil 500 mg 30 tabs.', 'ميفرونيل 500مجم 30 اقراص', '14', NULL, 'Nsaid.fenamates', 'Mefenamic acid', 'El nile.', 'Tab', 'أقراص', '500 mg', '3', NULL, 'about mefenamic acidsnsaid analgesic antiinflammatory.ssmechanism of action of mefenamic acidsmefenamic acid has analgesic anti-inflammatory and antipyretic action. it acts by inhibiting prostaglandin (pgs) synthesis and their release at the site of injur', NULL, NULL, 689, '2022-07-16', 'For the treatment of rheumatoid arthritis, osteoarthritis, dysmenorrhea, and mild to moderate pain, inflammation, and fever.', 'Mefenamic acid binds the prostaglandin synthetase receptors COX-1 and COX-2, inhibiting the action of prostaglandin synthetase. As these receptors have a role as a major mediator of inflammation and/or a role for prostanoid signaling in activity-dependent plasticity, the symptoms of pain are temporarily reduced.', 'Mefenamic acid, an anthranilic acid derivative, is a member of the fenamate group of nonsteroidal anti-inflammatory drugs (NSAIDs). It exhibits anti-inflammatory, analgesic, and antipyretic activities. Similar to other NSAIDs, mefenamic acid inhibits prostaglandin synthetase.', 'DrugBank', 0, 0, 0);
