INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13865, 'Alvesco 160mcg/actuation inhaler 120 doses', 'الفيسكو 160 مكجم / استنشاق 120 جرعة', '120', NULL, 'Bronchodilator.anti-inflammatory.glucocorticoid steroid', 'Ciclesonide', 'Nycomed', 'Inhaler', 'جهاز استنشاق', '160mcg', '1', NULL, 'description ciclesonide is a glucocorticoid used to treat obstructive airway diseases. indication for the treatment of nasal symptoms associated with seasonal and perennial allergic rhinitis in adults and adolescents 12 years of age and older. mechanism o', NULL, NULL, 1043, '2022-07-22', 'For the treatment of nasal symptoms associated with seasonal and perennial allergic rhinitis in adults and adolescents 12 years of age and older.', 'Glucocorticoids such as ciclesonide can inhibit leukocyte infiltration at the site of inflammation, interfere with mediators of inflammatory response, and suppress humoral immune responses. The antiinflammatory actions of glucocorticoids are thought to involve phospholipase A2 inhibitory proteins, lipocortins, which control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes. Ciclesonide reduces inflammatory reaction by limiting the capillary dilatation and permeability of the vascular structures. These compounds restrict the accumulation of polymorphonuclear leukocytes and macrophages and reduce the release of vasoactive kinins. Recent research suggests that corticosteroids may inhibit the release of arachidonic acid from phospholipids, thereby reducing the formation of prostaglandins. Ciclesonide is a glucocorticoid receptor agonist. On binding, the corticoreceptor-ligand complex translocates itself into the cell nucleus, where it binds to many glucocorticoid response elements (GRE) in the promoter region of the target genes. The DNA bound receptor then interacts with basic transcription factors, causing an increase or decrease in expression of specific target genes, including suppression of IL2 (interleukin 2) expression.', 'Ciclesonide is a pro-drug that is enzymatically hydrolyzed to a pharmacologically active metabolite, C21-desisobutyryl-ciclesonide (des-ciclesonide or RM1) following intranasal application. Des-ciclesonide has anti-inflammatory activity with affinity for the glucocorticoid receptor that is 120 times higher than the parent compound. The precise mechanism through which ciclesonide affects allergic rhinitis symptoms is not known. Corticosteroids have been shown to have a wide range of effects on multiple cell types (e.g., mast cells, eosinophils, neutrophils, macrophages, and lymphocytes) and mediators (e.g., histamine, eicosanoids, leukotrienes, and cytokines) involved in allergic inflammation.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2738, 'Clearalique forte 4% cream 30 gm', 'كليراليك فورت 4% 30جرام كريم', '15', '8', 'Whitening topical', 'Hydroquinone', 'Delta pharma', 'Cream', 'كريم', '4%', '1', 'النمش والكلف غير النشط-- آثار الشيخوخة -- كافة حالات تصبغ الميلانين غير المرغوبة، التي سببها الحمل، أو حبوب تنظيم الحمل، أو الأدوية الهرمونية، أو آثار اللإصابات والجروح، أو آثار التعرض للشمس -- علاج آثار حب الشباب.', 'about hydroquinone benzenediol derivative depigmenting agent. mechanism of action of hydroquinone hydroquinone decreases the formation of melanin in the skin. it inhibits tyrosinase and other melanin forming enzymes and thus decreases the formation and in', '6221163001091', NULL, 5905, '2023-12-19', 'Hydroquinone is used as an OTC topical lightening agent for disorders of hyperpigmentation including melasma, post-inflammatory hyperpigmention, sunspots and freckles. ', 'Hydroquinone reduces melanin pigment production through inhibition of the tyrosinase enzyme, which is involved in the initial step of the melanin pigment biosynthesis pathway. Hydroquinone takes several months to take effect. ', NULL, 'DrugBank', 0, 0, 0);
