INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28629, 'Tylecare anti-dandruff shampoo 200 ml', 'تيلكير شامبو 200مل', '50', NULL, 'Hair care', 'Selenium sulphide+sodium lauryl sulphate', 'Hi-care > castle care', 'Amp', 'أمبول', '200 ml', '1', NULL, 'once weekly', NULL, NULL, 692, '2022-07-15', 'For treatment of tinea versicolor, tinea capitis, dandruff and seborrheic dermatitis of the scalp.', 'Topical selenium sulfide may act by an antimitotic action, resulting in a reduction in the turnover of epidermal cells. It also has local irritant, antibacterial, and mild antifungal activity, which may contribute to its effectiveness. An antimitotic mechanism of action is suggested by data showing that selenium sulfide decreases the rate of incorporation of radioactively labeled thymidine into the DNA of dermal epithelial cells. The following organisms are generally considered susceptible to selenium sulfide in vitro: <i>Malassezia furfur</i>, <i>Microsporum sp.</i> including <i>Microsporum audouinii</i> and <i>Microsporum canis</i>, <i>Pityrosporon sp.</i>, <i>Trichophyton sp.</i> including <i>Trichophyton schoenleinii</i> and <i>Trichophyton tonsurans</i>. Selenium sulfide has been shown to be sporicidal to <i>T. tonsurans</i>, the most common etiologic agent of tinea capitis. One in-vitro study demonstrated that 2.5% selenium sulfide was equivalent in sporicidal activity to both 1% and 2% zinc pyrithione.', 'Selenium sulfide is an antifungal agent often used in shampoos for the treatment of dandruff and seborrheic dermatitis. Selenium sulfide is highly active in inhibiting the growth of <i>P. ovale</i>. It is also a proven cytostatic agent, slowing the growth of both hyperproliferative and normal cells in dandruff and seborrheic dermatitis. A 0.6% micronized form of selenium sulfide is also safe and effective for dandruff.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30203, 'Blefaritto eye contour gel 10 gm', 'بلفاريتو جل كونتور للعين 10 جم', '215', '140', 'Skin care.eye contour', 'Tea tree oil+chamomile oil', 'Jeomed pharmaceuticals > ultra vision', 'Gel', 'جل', '10 gm', '1', 'جل لإلتهابات الجفن وعلاج الحكة والإحمرار تدلك منطقة محيط العين بولسطة الجل لتكوين طبقة رقيقة مساءا قبل النوم وصباحا يستخدم لمنطقة محيط العين وجذر الرموش ولا يجب غسلها.', 'provides daily cleaning of eyelids eye area eyebrows and eyelashes. it also supports the reduction of dry itchy or irritated eye symptoms. oil pollen and other residues found on the eyelids increase the risk of irritation and blepharitis. eye gel; the pre', NULL, NULL, 2965, '2024-05-29', 'Indicated for topical use to help protect against infection in minor cuts, scrapes, and burns. No FDA-approved therapeutic indications.', 'The components of tea tree oil, particularly terpinen-4-ol and α-terpineol, mediate antimicrobial actions by disrupting the structural and functional integrity of bacterial membrane. Hydrocarbons are capable of partitioning into the cell and cytoplasmic membrane of microorganisms and disrupt their vital functions, which may result in leakage of ions such as potassium, and the inhibition of respiration [A32439]. Eventually, cell lysis may occur due to weakening of the cell wall, and loss of turgor pressure and subsequent rupture of the cytoplasmic membrane [A32441]. The loss of 260-nm-absorbing material may be indicative of a damaged cytoplasmic membrane and loss of nucleic acids [A32441]. In _E. coli_, perturbed potassium homeostasis, glucose-dependent respiration, cell morphology, and ability to exclude propidium iodide was observed. 



Tea tree oil also mediates its antifungal actions in a similar way, where it alters the permeability of Candida albicans and inhibits its respiration in a dose-dependent manner [A32439]. Plasma and mitochondrial membranes of fungal species are also thought to be negatively affected by inhibition of glucose-induced medium acidification by tea tree oil, which involves inhibition of membrane ATPase responsible for the expulsion of protons [A32439]. Tea tree oil also inhibits the formation of germ tubes, or mycelial conversion, in _C. albicans_, thereby disrupting cell morphogenesis [A32439]. Water-soluble fraction of TTO, terpinen-4-ol, and α-terpineol, can inhibit the lipopolysaccharide-induced production of the inflammatory mediators such as TNF-α, IL-1β and IL-10 by human peripheral monocytes by approximately 50% and that of prostaglandin E2 by about 30% after 40 h [A32439]. These components of tea tree oil may also suppress superoxide production by agonist-stimulated monocytes and decrease the production of reactive oxygen species by both stimulated neutrophils and monocytes [A32439].', 'Tea tree oil exhibits antibacterial, antifungal, antiviral, and antiprotozoal activities [A32439]. It mostly mediates bactericidal actions at concentrations of 1.0% or less in most bacteria such as _Staphylococcus aureus_ and _Escherichia coli_, and causes bacteriostatic effects at lower concentrations [A32439]. Organisms such as commensal skin staphylococci and micrococci, _Enterococcus faecalis_, and Pseudomonas aeruginosa_emphasized text_ were susceptible to tea tree oil concentrations of 2% [A32439]. It is proposed that water-soluble components of tea tree oil are capable in inducing anti-inflammatory actions; terpinen-4-ol attenuates the vasodilation and plasma extravasation associated with histamine-induced inflammation in humans [A32438].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27908, 'Tarion shampoo anti dandruff 200 ml', 'تاريون شامبو مضاد لقشرة الشعر 200 مل', '195', '110', 'Anti dandruff', 'Panthenol+olive oil+tar+piroctone+olamine+zinc pyrithione+keratin', 'Troya pharma', 'Amp', 'أمبول', '200 ml', '1', NULL, NULL, NULL, NULL, 1300, '2025-07-31', NULL, NULL, NULL, NULL, 0, 0, 0);
