INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18899, 'Feldene 20mg 5 supp.', 'فلدين 20مجم 5 اقماع لبوس', '24', NULL, 'Nsaid.oxicam', 'Piroxicam', 'Pfizer', 'Supp', 'لبوس', '20mg', '1', NULL, 'about piroxicam nsaid oxicam derivative analgesic antiinflammatory. mechanism of action of piroxicam drug inhibits the biosynthesis and release of prostaglandins by inhibiting cyclooxygenase enzyme in a reversible manner. in this way it reduces the pg (pr', '6221180000374', NULL, 943, '2022-12-05', 'For treatment of osteoarthritis and rheumatoid arthritis.', 'The antiinflammatory effect of Piroxicam may result from the reversible inhibition of cyclooxygenase, causing the peripheral inhibition of prostaglandin synthesis. The prostaglandins are produced by an enzyme called Cox-1. Piroxicam blocks the Cox-1 enzyme, resulting into the disruption of production of prostaglandins. Piroxicam also inhibits the migration of leukocytes into sites of inflammation and prevents the formation of thromboxane A2, an aggregating agent, by the platelets.', 'Piroxicam is in a class of drugs called nonsteroidal anti-inflammatory drugs (NSAIDs). Piroxicam works by reducing hormones that cause inflammation and pain in the body. Piroxicam is used to reduce the pain, inflammation, and stiffness caused by rheumatoid arthritis and osteoarthritis.', 'DrugBank', 0, 0, 0);
