INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33143, 'Malignomide 50 mg 28 f.c. tabs.', 'ماليجنوميد 50مجم 28 قرص', '826', '468', 'Antineoplastic', 'Bicalutamide', 'Hikma specialized pharmaceuticals', 'Tab', 'أقراص', '50 mg', '4', 'علاج سرطان البروستاتا.', 'about bicalutamide non steroidal anti estrogen anti neoplastic. mechanism of action of bicalutamide this antiandrogenic drug binds to the receptor of androgens on the cells of prostate gland. thus it blocks the action of androgen on prostate gland and pre', NULL, NULL, 1862, '2025-05-02', 0, 'Bicalutamide is indicated in combination with a luteinizing hormone-releasing hormone (LHRH) agonist for the treatment of Stage D2 metastatic carcinoma of the prostate.[L40478]', 'Bicalutamide competes with androgen for the binding of androgen receptors, consequently blocking the action of androgens of adrenal and testicular origin which stimulate the growth of normal and malignant prostatic tissue.', 'Bicalutamide is an antineoplastic hormonal agent primarily used in the treatment of prostate cancer. Bicalutamide is a pure, nonsteroidal anti-androgen with affinity for androgen receptors (but not for progestogen, estrogen, or glucocorticoid receptors). Consequently, Bicalutamide blocks the action of androgens of adrenal and testicular origin which stimulate the growth of normal and malignant prostatic tissue. Prostate cancer is mostly androgen-dependent and can be treated with surgical or chemical castration. To date, antiandrogen monotherapy has not consistently been shown to be equivalent to castration.', 'DrugBank', 0, 0, 0);
