INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33449, 'Pyridovit 30 caps.', 'بيريدوفيت 30 كبسولة', '150', '140', 'Vitamin b complex', 'Vitamin b1 (benfotiamine)+vitamin b2+vitamin b6+vitamin b9+vitamin b12+vitamin b5', 'Egypharma', 'Cap', 'كبسولة', NULL, '3', 'مكمل غذائى يستخدم فى دعم الجهاز العصبى وزيادة الطاقة', NULL, '6224001820311', NULL, 3591, '2024-08-29', 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33450, 'Pyrto 30 tabs.', 'بيرتو 30 قرص', '95', '85', 'Multivitamins', 'Iron+lactoferrin+zinc+follic acid+vit b1+vit b2+vit b6+vit b12', 'Auc pharma', 'Tab', 'أقراص', NULL, '3', NULL, 'multivitamins iron+lactoferrin+zinc+follic acid+vit b1+vit b2+vit b6+vit b12', NULL, NULL, 1548, '2025-08-01', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33452, 'Q-rizm 30 caps.', 'كيو-ريزم 30 كبسولة', '480', '405', 'Multivitamin', 'Coenzyme q10+vitamin d3', 'New boston pharmaceuticals', 'Cap', 'كبسولة', NULL, '3', 'مكمل غذائي يدعم صحة القلب و الاوعية الدموية .', NULL, '6224010895027', NULL, 3400, '2025-12-05', 0, 'Mesna is a uroprotective agent and is used prophylactically to reduce ifosfamide and cyclophosphamide induced hemorrhagic cystitis.[A18572]', 'A metabolite called acrolein is produced when ifosfamide and cyclophosphamide are metabolized. This metabolite concentrates in the bladder and causes cell death via upregulation of reactive oxygen species (ROS), and activates inducible nitric oxide synthase (iNOS) which leads to production of nitric oxide (NO). Both ROS and NO produce products which are detrimental to lipids, proteins and DNA strands. Furthermore, ROS stimulate gene expression of pro-inflammatory cytokines such as TNF-α AND IL-1β. Acrolein may also lead to ulceration of the bladder urothelium. Mesna protects against cyclophosphamide and ifosfamide induced hemorrhagic cystitis by binding to their toxic metabolites. Mesna is metabolized to dimesna and excreted by the kidneys. Glutathione dihydrogenase acts on the reabsorbed portion and produces free sulfhydryl groups. These free sulfhydryl  groups bind acrolein in the bladder, allowing effective excretion and prevention  of toxic effects.[A18570] In addition, Mesna binds to and detoxifies a urotoxic ifosfamide metabolite called 4-hydroxy-ifosfamide.', 'Mesna binds to and inactivates acrolein there by preventing or reducing bladder problems', 'DrugBank', 0, 0, 0);
