INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323741, 'Dolutegravir', 'Atazanavir', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations of dolutegravir. The proposed mechanism is atazanavir inhibition of UGT1A1 and CYP450 3A4, the isoenzymes primarily responsible for the metabolic clearance of dolutegravir.', 'DDInter', 1767317474, 'Because safety data regarding increased dolutegravir exposures are limited, caution may be advisable if dolutegravir is used in combination with atazanavir, with or without ritonavir.', 'Metabolism', 'Because safety data regarding increased dolutegravir exposures are limited, caution may be advisable if dolutegravir is used in combination with atazanavir, with or without ritonavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
