INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323769, 'Clofarabine', 'Lamivudine', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', 'DDInter', 1767317474, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
