INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323784, 'Dofetilide', 'Dolutegravir', 'Major', 'Coadministration with dolutegravir may increase the plasma concentrations of dofetilide. The proposed mechanism is dolutegravir inhibition of renal organic cation transporter (OCT2) and potentially multidrug and toxin extrusion transporter (MATE) 1. Dolutegravir has been shown in vivo to inhibit tubular secretion of creatinine by inhibiting OCT2. In vitro, dolutegravir inhibits both OCT2 and MATE1. Clinically, high plasma levels of dofetilide may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317474, 'Given the risk for serious and/or life-threatening cardiovascular events associated with increased dofetilide plasma levels, concomitant use with dolutegravir is considered contraindicated.', 'Excretion', 'Given the risk for serious and/or life-threatening cardiovascular events associated with increased dofetilide plasma levels, concomitant use with dolutegravir is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
