INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323843, 'Dolutegravir', 'Nevirapine', 'Major', 'Coadministration with nevirapine may theoretically decrease the plasma concentrations of dolutegravir. The interaction has not been specifically studied. However, dolutegravir is partially metabolized by CYP450 3A4, and nevirapine is a known inducer of the isoenzyme.', 'DDInter', 1767317474, 'Coadministration of dolutegravir with nevirapine should be avoided because there are insufficient data to make dosing recommendations.', 'Metabolism', 'Coadministration of dolutegravir with nevirapine should be avoided because there are insufficient data to make dosing recommendations.', NULL, NULL, 'Major', 'pharmacodynamic');
