INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323861, 'Dolutegravir', 'Rifampicin', 'Major', 'Coadministration with potent inducers of UGT1A and CYP450 3A4 isoenzymes such as rifampin may significantly decrease the plasma concentrations of dolutegravir, which is primarily metabolized by UGT1A1 with some contribution from CYP450 3A4. Dolutegravir is also a substrate of UGT1A3, UGT1A9, and P-glycoprotein in vitro.', 'DDInter', 1767317474, 'When prescribed in combination with rifampin, the dosage of dolutegravir should be increased to 50 mg twice daily for both adults and pediatric patients 12 years of age and older who weigh at least 40 kg. The safety and efficacy of dosages above 50 mg twice daily have not been evaluated. For concomitant use of rifampin with fixed-dose combination products containing dolutegravir, it is recommended to administer an additional 50 mg daily dose of dolutegravir approximately 12 hours from the combination product. Alternatives to rifampin that are not metabolic inducers should be considered whenever possible for integrase strand transfer inhibitor (INSTI)-experienced patients with certain INSTI-associated resistance substitutions or clinically suspected INSTI resistance.', 'Metabolism', 'When prescribed in combination with rifampin, the dosage of dolutegravir should be increased to 50 mg twice daily for both adults and pediatric patients 12 years of age and older who weigh at least 40 kg.', NULL, NULL, 'Major', 'pharmacodynamic');
