INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323922, 'Betamethasone', 'Zidovudine', 'Moderate', 'Prolonged use of certain nucleoside reverse transcriptase inhibitors may commonly cause myopathy, including rare cases of rhabdomyolysis. It is not known if the risk of myopathy is increased during concurrent administration of other agents associated with myopathy, including corticosteroids, fibric acid derivatives, HMG-CoA reductase inhibitors, chloroquine, hydroxychloroquine, penicillamine, and lipid-lowering doses of niacin.', 'DDInter', 1767317474, 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together. Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever.', 'Synergism', 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
