INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323947, 'Cimetidine', 'Zidovudine', 'Minor', 'Cimetidine has been shown to significantly decrease (by 56%) the renal clearance of zidovudine, most likely by competition for active tubular secretion. However, serum levels of zidovudine and its primary metabolite were not affected. Since zidovudine is mostly metabolized by the liver this interaction is unlikely to be of clinical significance except in patients with hepatic impairment.', 'DDInter', 1767317474, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
