INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323973, 'Didanosine', 'Zidovudine', 'Minor', 'In one study, concomitant use of zidovudine and didanosine increased zidovudine elimination half-life by 18% and increased the area under the plasma concentration-time curve by 35%. However, several other studies (including one pediatric study) failed to demonstrate significant pharmacokinetic alterations of either drug when zidovudine and didanosine were administered concurrently. A consistent pharmacokinetic interaction does not appear to exist between these two drugs, but it may be appropriate to monitor for increased toxicities during simultaneous therapy.', 'DDInter', 1767317474, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
