INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323987, 'Estazolam', 'Zidovudine', 'Minor', 'One study has suggested that patients receiving zidovudine and a benzodiazepine concomitantly may demonstrate increased plasma clearance of the benzodiazepine. That study also has suggested that patients on concomitant therapy may experience an increased number of headaches.', 'DDInter', 1767317474, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323988, 'Etanercept', 'Zidovudine', 'Major', 'The use of tumor necrosis factor (TNF) blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Serious infections and sepsis, including fatalities, have been reported with the use of TNF blockers, particularly in patients on concomitant immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. Concomitant use of TNF blockers with other immunosuppressants such as azathioprine or mercaptopurine may also increase the risk of a rare and often fatal cancer of white blood cells known as hepatosplenic T-Cell lymphoma (HSTCL).', 'DDInter', 1767317474, 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections. TNF blocker therapy should be discontinued if a serious infection or sepsis occurs. Close monitoring for signs and symptoms of HSTCL (e.g., splenomegaly, hepatomegaly, abdominal pain, persistent fever, night sweats, weight loss) is also recommended during use of TNF blockers, particularly in combination with other immunosuppressants such as azathioprine and mercaptopurine.', 'Synergism', 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323989, 'Ethinylestradiol', 'Zidovudine', 'Minor', 'Ethinyl estradiol has been shown to inhibit the glucuronidation of zidovudine in vitro. A clinically significant interaction has not been demonstrated, however.', 'DDInter', 1767317474, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323990, 'Ethotoin', 'Zidovudine', 'Minor', 'One study has suggested that zidovudine may have variable effects on plasma phenytoin levels. Increased levels, decreased levels, and no change have been reported. If zidovudine and phenytoin must be used together, close observation for altered clinical and laboratory effects is recommended.', 'DDInter', 1767317474, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1323991, 'Etodolac', 'Zidovudine', 'Moderate', 'Limited clinical data suggest that the concomitant use of zidovudine with nonsteroidal anti-inflammatory agents may increase the risk of hematological toxicity and/or bleeding in HIV-positive patients with hemophilia.', 'DDInter', 1767317474, 'It is recommended to monitor patients for side effects and bleeding during concomitant use.', 'Synergism', 'It is recommended to monitor patients for side effects and bleeding during concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
