INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (13, 'Clofarabine', 'Abacavir', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14, 'Cobicistat', 'Abacavir', 'Moderate', 'Cobicistat may increase the plasma concentrations of antiretroviral agents. The plasma concentrations of cobicistat may also be increased or reduced in the presence of antiretroviral agents. The proposed mechanism is cobicistat inhibition of the CYP450 3A4 isoenzyme, of which antiretroviral agents may be substrates, and the inhibition or induction of CYP450 3A4 by concomitant antiretroviral medications.', NULL, 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor.', NULL, 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor. In addition, cobicistat should not be used concomitantly with ritonavir due to their similar effects on CYP450 3A4. According to some authorities, use of the antiretroviral combinations of atazanavir-cobicistat or darunavir-cobicistat concomitantly with the CYP450 3A4 inducers efavirenz, etravirine, or nevirapine is also not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[3] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim, Ridgefield, CT.[4] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[5] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Prezcobix (cobicistat-darunavir)." Janssen Pharmaceuticals, Titusville, NJ.[8] Cerner Multum, Inc. "Australian Product Information." O 0[9] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[10] "Product Information. Tybost (cobicistat)." Gilead Sciences, Foster City, CA.[11] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[12] Cerner Multum, Inc. "Australian Product Information." O 0[13] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[14] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.', 1767365203);
