INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (40, 'Aminoglutethimide', 'Dolutegravir', 'Minor', 'Coadministration with inducers of UGT1A and CYP450 3A4 isoenzymes may decrease the plasma concentrations of dolutegravir, which is primarily metabolized by UGT1A1 with some contribution from CYP450 3A4. Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution may be advisable if dolutegravir is used in combination with mild or moderate UGT1A/CYP450 3A4 inducers.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40/', '[1] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (41, 'Amiodarone', 'Dolutegravir', 'Minor', 'Theoretically, coadministration with inhibitors of UGT1A or CYP450 3A4 isoenzymes may increase the plasma concentrations of dolutegravir, which is primarily metabolized by UGT1A1 with some contribution from CYP450 3A4. Because safety data regarding increased dolutegravir exposures are limited, caution may be advisable if dolutegravir is used in combination with both a UGT1A1 inhibitor and a CYP450 3A4 inhibitor.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41/', '[1] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.[2] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.', 1767365203);
