INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180, 'Dolutegravir', 'St. John''s Wort', 'Major', 'Coadministration with potent inducers of UGT1A1 and CYP450 3A4 isoenzymes may significantly decrease the plasma concentrations of dolutegravir, which is primarily metabolized by UGT1A1 with some contribution from CYP450 3A4. Dolutegravir is also a substrate of UGT1A3, UGT1A9, and P-glycoprotein in vitro.', NULL, 'In patients with HIV-1 without integrase inhibitor (INI) resistance, some authorities recommend a dolutegravir dose of 50 mg twice daily for both adults and pediatric patients 12 years of age and older who weigh at least 40 kg when coadministered with potent UGT1A1 and CYP450 3A4 inducers including carbamazepine, phenytoin, phenobarbital, and St.', NULL, 'In patients with HIV-1 without integrase inhibitor (INI) resistance, some authorities recommend a dolutegravir dose of 50 mg twice daily for both adults and pediatric patients 12 years of age and older who weigh at least 40 kg when coadministered with potent UGT1A1 and CYP450 3A4 inducers including carbamazepine, phenytoin, phenobarbital, and St. John''s Wort. However, other authorities advise that coadministration of dolutegravir with these inducers should be avoided. The safety and efficacy of dosages above 50 mg twice daily have not been evaluated. Alternative treatment combinations that do not include metabolic inducers should be considered whenever possible for INI-experienced patients with certain INI-associated resistance substitutions or clinically suspected INI resistance. In addition, concomitant use of potent UGT1A1 and CYP450 3A4 inducers with fixed-dose combination products containing dolutegravir is not recommended; however, when used in combination with carbamazepine, some authorities advise administration of an additional 50 mg daily dose of dolutegravir approximately 12 hours from the combination product. Local antiretroviral treatment experts should be consulted for current practice.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180/', '[1] "Product Information. Dovato (dolutegravir-lamivudine)." ViiV Healthcare, Research Triangle Park, NC.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.[5] "Product Information. Triumeq (abacavir/dolutegravir/lamiVUDine)." ViiV Healthcare, Research Triangle Park, NC.[6] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[7] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181, 'Dolutegravir', 'Stiripentol', 'Minor', 'Theoretically, coadministration with inhibitors of UGT1A or CYP450 3A4 isoenzymes may increase the plasma concentrations of dolutegravir, which is primarily metabolized by UGT1A1 with some contribution from CYP450 3A4. Because safety data regarding increased dolutegravir exposures are limited, caution may be advisable if dolutegravir is used in combination with both a UGT1A1 inhibitor and a CYP450 3A4 inhibitor.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181/', '[1] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.[2] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.', 1767365203);
