INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190, 'Dolutegravir', 'Tipranavir', 'Major', 'Coadministration with potent inducers of UGT1A3 and CYP450 3A4 isoenzymes such as tipranavir/ritonavir may significantly decrease the plasma concentrations of dolutegravir, which is primarily metabolized by UGT1A1 with some contribution from CYP450 3A4. Dolutegravir is also a substrate of UGT1A3, UGT1A9, and P-glycoprotein in vitro.', NULL, 'When prescribed in combination with tipranavir/ritonavir, the dosage of dolutegravir should be increased to 50 mg twice daily for both adults and pediatric patients 12 years of age and older who weigh at least 40 kg.', NULL, 'When prescribed in combination with tipranavir/ritonavir, the dosage of dolutegravir should be increased to 50 mg twice daily for both adults and pediatric patients 12 years of age and older who weigh at least 40 kg. The safety and efficacy of dosages above 50 mg twice daily have not been evaluated. Concomitant use of tipranavir/ritonavir with the fixed-dose combination product containing dolutegravir/lamivudine is not recommended. For concomitant use of tipranavir/ritonavir with the fixed-dose combination product containing abacavir/dolutegravir/lamivudine, it is recommended to administer an additional dose of dolutegravir 50 mg/day, separated from the combination product by 12 hours. Alternative treatment combinations that do not include metabolic inducers should be considered whenever possible for integrase strand transfer inhibitor (INSTI)-experienced patients with certain INSTI-associated resistance substitutions or clinically suspected INSTI resistance.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190/', '[1] "Product Information. Dovato (dolutegravir-lamivudine)." ViiV Healthcare, Research Triangle Park, NC.[2] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.[3] "Product Information. Triumeq (abacavir/dolutegravir/lamiVUDine)." ViiV Healthcare, Research Triangle Park, NC.[4] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Cerner Multum, Inc. "Australian Product Information." O 0[9] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (191, 'Trabectedin', 'Lamivudine', 'Moderate', 'Coadministration of trabectedin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191/', '[1] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (192, 'Trimethoprim', 'Lamivudine', 'Minor', 'In a study with 14 HIV-positive patients, coadministration of trimethoprim/sulfamethoxazole (trimethoprim/sulfamethoxazole DS once a day for 5 days) and lamivudine (300 mg single dose on day 5) resulted in a mean decrease of 35% in lamivudine renal clearance and a mean increase of 43% in lamivudine area under the plasma concentration-time curve. The mechanism of interaction is thought to be competitive inhibition of tubular secretion by trimethoprim. Lamivudine did not affect the pharmacokinetic profile of trimethoprim/sulfamethoxazole. Given the favorable safety profile of lamivudine, this interaction is unlikely to be of clinical significance. However, patients with renal dysfunction should be monitored carefully and the lamivudine dose adjusted if necessary. In addition, it should be noted that the effect of higher dosages of trimethoprim/sulfamethoxazole on lamivudine pharmacokinetics has not been investigated.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192/', '[1] Moore KHP, Yuen GJ, Raasch RH, Eron JJ, Martin D, Mydlow PK, Hussey EK "Pharmacokinetics of lamivudine administered alone and with trimethoprim-sulfamethoxazole." Clin Pharmacol Ther 59 (1996): 550-8[2] "Product Information. Epivir (lamivudine)." Glaxo Wellcome, Research Triangle Park, NC.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Tivicay (dolutegravir)." ViiV Healthcare, Research Triangle Park, NC.', 1767365203);
