INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (425, 'Zidovudine', 'Pyrimethamine', 'Moderate', 'Coadministration of pyrimethamine with other antifolate or myelosuppressive agents may have additive effects on rapidly proliferating tissues such as bone marrow, possibly increasing the risk of megaloblastic anemia and other hematologic adverse effects.', NULL, 'Caution is advised if pyrimethamine is prescribed in combination with other antifolate or myelosuppressive agents.', NULL, 'Caution is advised if pyrimethamine is prescribed in combination with other antifolate or myelosuppressive agents. Complete blood cell counts should be closely monitored. Treatment interruption or dose reduction may be required if toxicities such as mucositis or myelosuppression develop. Patients should be advised to contact their physician if they develop oral ulcers, painful swallowing, vomiting, diarrhea, and/or signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/425/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365203);
