INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (448, 'Zidovudine', 'Sirolimus', 'Moderate', 'Coadministration of zidovudine with other bone marrow depressive or cytotoxic agents may increase the risk and/or severity of zidovudine hematologic toxicity. Zidovudine has been associated with hematologic toxicities such as: neutropenia, granulocytopenia and severe anemia that required blood transfusions particularly in patients with advanced HIV disease.', NULL, 'Hematological parameters should be closely monitored.', NULL, 'Hematological parameters should be closely monitored. Some authorities recommend dosages of one or both agents may be adjusted if necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/448/', '[1] "Product Information. Retrovir (zidovudine)." Glaxo Wellcome, Research Triangle Park, NC.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Agencia Española de Medicamentos y Productos Sanitarios Healthcare "Centro de información online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[5] "Product Information. Retrovir (zidovudine)." Glaxo Wellcome, Research Triangle Park, NC.[6] Unadkat JD, Collier AC, Crosby SS, et al "Pharmacokinetics of oral zidovudine (azidothymidine) in patients with AIDS when administered with and without a high-fat meal." AIDS 4 (1990): 229-32[7] Sahai J, Gallicano K, Garber G, et al. "The effect of a protein meal on zidovudine pharmacokinetics in HIV-infected patients." Br J Clin Pharmacol 33 (1992): 657-60[8] Lotterer E, Ruhnke M, Trautman M, et al "Decreased and variable systemic availability of zidovudine in patients with AIDS if administered with a meal." Eur J Clin Pharmacol 40 (1991): 305-8', 1767365203);
