INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (957, 'Abciximab', 'Alteplase', 'Major', 'The intended and unintended effects of glycoprotein IIb/IIIa inhibitors and thrombolytic agents are additive, such that there is an increased frequency of major bleeding complications, including intracranial hemorrhage, retroperitoneal bleeding, spontaneous gastrointestinal (GI) and genitourinary (GU) bleeding among patients receiving both agents compared with the risk associated with the use of either agent alone.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. The successful use of combination therapy with glycoprotein IIb/IIIa inhibitors and reduced doses of thrombolytics has been reported in clinical trials. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Thrombolytic and/or glycoprotein inhibitor therapy should be discontinued immediately if serious or uncontrollable bleeding occurs.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/957/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[4] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (958, 'Anagrelide', 'Abciximab', 'Major', 'The combination of glycoprotein IIb/IIIa inhibitors and anagrelide can theoretically significantly increase the risk of bleeding. GP IIb/IIIa inhibitors inhibit platelet aggregation by blocking the fibrinogen, von Willebrand factor, and other adhesive ligands to platelets. The mechanism whereby anagrelide decreases platelet concentrations is not known. The combination of these drugs, therefore, could decrease platelet number and function.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations. In addition, invasive procedures, such as urinary catheterization, nasogastric or nasotracheal intubation, and even venous punctures should be avoided or minimized. Non-compressible venous access sites, such as jugular or subclavian sites, should be avoided.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/958/', '[1] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company, Indianapolis, IN.', 1767363020);
