INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (973, 'Abciximab', 'Capsicum', 'Minor', 'Theoretically, capsicum may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Animal and limited human data suggest that capsicum may increase fibrinolytic activity, lower plasma fibrinogen levels, increase plasma antithrombin III levels, inhibit platelet aggregation, and prolong bleeding time. In patients who have used capsicum extensively prior to receiving anticoagulation, antiplatelet, or thrombolytic therapy, the potential for an interaction should be considered. Clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/973/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000): 1221-7; quiz 1228-30', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (974, 'Abciximab', 'Celecoxib', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents. Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/974/', '[1] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (975, 'Abciximab', 'Chamomile', 'Minor', 'Some herbs such as chamomile and fenugreek have coumarin constituents. Theoretically, ingesting large quantities of these herbs may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. However, their effects on the coagulation system have not been studied, and bleeding complications have not been reported in humans. Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used chamomile or fenugreek extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/975/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000): 1221-7; quiz 1228-30[2] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998): 2200-11[3] Lambert JP, Cormier J "Potential interaction between warfarin and boldo-fenugreek." Pharmacotherapy 21 (2001): 509-12', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (976, 'Abciximab', 'Choline salicylate', 'Moderate', 'Although aspirin is routinely given with glycoprotein IIb/IIIa inhibitors, be cognizant of the increased risk of bleeding associated with this combination due to the additive inhibitory effects of salicylates on platelet function.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Therapy should be discontinued immediately if serious or uncontrollable bleeding or thrombocytopenia occurs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/976/', '[1] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001): 1S-2S[2] Klinkhardt U, Kirchmaier CM, Westrup D, Graff J, Mahnel R, Breddin HK, Harder S "Ex vivo-in vitro interaction between aspirin, clopidogrel, and the glycoprotein IIb/IIIa inhibitors abciximab and SR121566A." Clin Pharmacol Ther 67 (2000): 305-13[3] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.[4] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company, Indianapolis, IN.', 1767363020);
