INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (998, 'Abciximab', 'Dipyridamole', 'Major', 'The combination of glycoprotein IIb/IIIa (GP IIb/IIIa) inhibitors and other drugs that inhibit platelet aggregation may significantly increase the risk of bleeding, especially from arterial puncture sites. The mechanism is synergistic pharmacologic effects.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations. In addition, invasive procedures, such as urinary catheterization, nasogastric or nasotracheal intubation, and even venous punctures should be avoided or minimized. Non-compressible venous access sites, such as jugular or subclavian sites, should be avoided.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/998/', '[1] Klinkhardt U, Kirchmaier CM, Westrup D, Graff J, Mahnel R, Breddin HK, Harder S "Ex vivo-in vitro interaction between aspirin, clopidogrel, and the glycoprotein IIb/IIIa inhibitors abciximab and SR121566A." Clin Pharmacol Ther 67 (2000): 305-13[2] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.[3] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company, Indianapolis, IN.[4] O''Donoghue M, Antman EM, Braunwald E, et al. "The efficacy and safety of prasugrel with and without a glycoprotein IIb/IIIa inhibitor in patients with acute coronary syndromes undergoing percutaneous intervention: a TRITON-TIMI 38 (Trial to Assess Improvement in Therapeutic Outcomes by Optimizing Pla" J Am Coll Cardiol 54 (2009): 678-85[5] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.', 1767363020);
