INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1246, 'Abemaciclib', 'Saquinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of abemaciclib and its pharmacologically active metabolites, all of which are substrates of the isoenzyme.', NULL, 'When used with potent CYP450 3A4 inhibitors other than ketoconazole (which is not recommended), patients who would otherwise be on abemaciclib 200 mg or 150 mg twice daily should have their dosage reduced to 100 mg twice daily.', NULL, 'When used with potent CYP450 3A4 inhibitors other than ketoconazole (which is not recommended), patients who would otherwise be on abemaciclib 200 mg or 150 mg twice daily should have their dosage reduced to 100 mg twice daily. In patients who have already had a dosage reduction to 100 mg twice daily due to adverse reactions, a further reduction of the abemaciclib dosage to 50 mg twice daily is recommended. Following discontinuation of the potent CYP450 3A4 inhibitor, abemaciclib dosage should be increased (after 3 to 5 half-lives of the inhibitor) to the dosage that was used before starting the inhibitor.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Tivozanib, Trametinib, Dacomitinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1246/', '[1] "Product Information. Verzenio (abemaciclib)." Lilly, Eli and Company, Indianapolis, IN.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Sy