INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1301, 'Anagrelide', 'Abiraterone', 'Major', 'Anagrelide can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided. Patients receiving anagrelide should have a cardiovascular examination, including an ECG, prior to initiation of treatment and monitored for cardiovascular effects during treatment. Hypokalemia or hypomagnesemia must be corrected prior to therapy and electrolytes monitored periodically during therapy.', 'Synergism', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, More', 'Darolutamide, Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1301/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation, Eatontown, NJ.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol inter