INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1628, 'Abiraterone', 'Timolol (ophthalmic)', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase the systemic effects of topically administered timolol, which is metabolized by the isoenzyme. Following ocular administration, timolol is systemically absorbed and can reach plasma levels associated with adverse beta-adrenergic blocking effects such as bronchospasm, depression, bradycardia, and hypotension. The risk may be increased if clearance of the drug is significantly diminished by concomitant CYP450 2D6 inhibitors.', NULL, 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors.', NULL, 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors. Particular caution is warranted in elderly patients, since they are generally more susceptible to adverse effects of topically administered beta blockers.', 'Metabolism', 'Acebutolol, Labetalol, Nadolol, Esmolol, Betaxolol, Penbutolol, Bisoprolol, Carteolol, Atenolol, Neostigmine, Brimonidine, More', 'Darolutamide, Tamoxifen, Anastrozole, Aminoglutethimide, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Enzalutamide, Relugolix, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1628/', '[1] Fraunfelder FT, Fraunfelder FW; Randall JA "Drug-Induced Ocular Side Effects 5th" Boston, MA: Butterworth-H