INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1999, 'Ritonavir', 'Paclitaxel', 'Major', 'Coadministration with certain antiretroviral agents such as protease inhibitors and delavirdine may increase the plasma concentrations and pharmacologic effects of paclitaxel. The proposed mechanism is inhibition of the CYP450 3A4-mediated metabolism of paclitaxel.', NULL, 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine.', NULL, 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine. A lower initial dosage of paclitaxel may be appropriate. Patients should be closely monitored for the development of dose-related paclitaxel toxicity such as myelosuppression, stomatitis, arthralgia, myalgia, visual disturbances and peripheral neuropathy, and the paclitaxel dosage further adjusted as necessary. Use of repaglinide with the fixed combination of atazanavir-cobicistat is not recommended.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, More', 'Topotecan, Teniposide', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1999/', '[1] Bundow D, Aboulafia DM "Potential drug interaction with paclitaxel and highly active antiretroviral therapy in two patients with AIDS-associated Kaposi sarcoma." Am J Clin Oncol 27 (2004): 81-4[2] "Product Information. Taxol (paclitaxel)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Taxotere (docetaxel)." Rhone-Poulenc Rorer, Collegeville, PA.[4] Panday VRN, Hoetelmans RMW, vanHeeswijk RPG, Meenhorst PL, Inghels M, Mulder JW, Beijnen JH "Paclitaxel in the treatment of human immunodeficiency virus 1-associated Kaposi''s sarcoma - drug-drug interactions with protease inhibitors and a nonnucleoside reverse transcriptase inhibitor: a case report study." Cancer Chemother Pharmacol 43 (1999): 516-9[5] Schwartz JD "Potential interaction of antriretroviral therapy with paclitaxel in patients with AIDS-related Kaposi''s sarcoma." AIDS 13 (1999): 283-4[6] Gill PS, Tulpule A, Espina BM, Cabriales S, Bresnahan J, Ilaw M, Louie S, Gustafson NF, Brown MA, Orcutt C, Winograd B, Scad "Paclitaxel is safe and effective in the treatment of advanced AIDS-related Kaposi''s sarcoma." J Clin Oncol 17 (1999): 1876-83[7] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[8] "Product Information. Taxotere (docetaxel)." Rhone-Poulenc Rorer, Collegeville, PA.[9] Starr SP, Hammann F, Gotta V, et al. "Pharmacokinetic interaction between taxanes and amiodarone leading to severe toxicity." Br J Clin Pharmacol 450 (2016): 22-27[10] Cerner Multum, Inc. "Australian Product Information." O 0[11] Yong WP, Wang LZ, Tham LS, et al. "A phase I study of docetaxel with ketoconazole modulation in patients with advanced cancers." Cancer Chemother Pharmacol 62 (2008): 243-51[12] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[13] Aronson JK, Grahame-Smith DG "Clinical pharmacology: adverse drug interactions." Br Med J 282 (1981): 288-91[14] Valenzuela B, Rebollo J, Perez T, Brugarolas A, Perez-Ruixo JJ "Effect of grapefruit juice on the pharmacokinetics of docetaxel in cancer patients: a case report." Br J Clin Pharmacol (2011):[15] McInnes GT, Brodie MJ "Drug interactions that matter: a critical reappraisal." Drugs 36 (1988): 83-110[16] Engels FK, Mathot RA, Loos WJ, van Schaik RH, Verweij J "Influence of high-dose ketoconazole on the pharmacokinetics of docetaxel." Cancer Biol Ther 5 (2006): 833-9[17] "Product Information. Incivek (telaprevir)." Vertex Phar