INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324127, 'Zidovudine', 'Rilonacept', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317474, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324128, 'Zidovudine', 'Ritonavir', 'Minor', 'Coadministration of ritonavir (300 mg every six hours) has been reported to decrease the area under the plasma concentration-time curve of zidovudine (200 mg every eight hours) by 25%. The plasma level of ritonavir has been reported not to have been affected. The mechanism of this interaction may be related to the induction of hepatic glucuronosyltransferase activities by ritonavir, which may enhance the glucuronidation of zidovudine. Adjustments in zidovudine dosage are unlikely to be necessary.', 'DDInter', 1767317474, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
