INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324142, 'Zidovudine', 'Sodium phosphate, monobasic (p32)', 'Moderate', 'The concomitant use of bone marrow depressants and sodium phosphate P-32 may have additive myelosuppressive effects.', 'DDInter', 1767317474, 'Patients should be monitored for excessive bone marrow suppression during treatment with sodium phosphate P-32. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with sodium phosphate P-32.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324143, 'Zidovudine', 'Stavudine', 'Moderate', 'In vitro data have demonstrated an antagonistic relationship between zidovudine and stavudine. The proposed mechanism is competition for phosphorylating enzymes required for nucleoside activation. A 41% reduction in active stavudine metabolites was reported when zidovudine was administered with stavudine. No effect on zidovudine activity was noted. Reduced stavudine activation and loss of antiviral activity due to prior zidovudine modification of intracellular phosphorylation has been suggested, but this was not confirmed by other studies. Further clarification is needed.', 'DDInter', 1767317474, 'The manufacturer recommends against concurrent use.', 'Antagonism', 'The manufacturer recommends against concurrent use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324144, 'Zidovudine', 'Streptozocin', 'Moderate', 'Coadministration of zidovudine with other bone marrow depressive or cytotoxic agents may increase the risk and/or severity of zidovudine hematologic toxicity. Zidovudine has been associated with hematologic toxicities such as: neutropenia, granulocytopenia and severe anemia that required blood transfusions particularly in patients with advanced HIV disease.', 'DDInter', 1767317474, 'Hematological parameters should be closely monitored. Some authorities recommend dosages of one or both agents may be adjusted if necessary.', 'Synergism', 'Hematological parameters should be closely monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324145, 'Zidovudine', 'Strontium chloride Sr-89', 'Moderate', 'The concomitant use of bone marrow depressants and strontium-89 chloride may have additive myelosuppressive effects.', 'DDInter', 1767317474, 'Patients should be monitored for excessive bone marrow suppression during treatment with strontium-89 chloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with strontium-89 chloride.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324146, 'Zidovudine', 'Sulfamethoxazole', 'Minor', 'Limited data indicate that zidovudine may significantly increase the serum half-life of both trimethoprim (TMP) and sulfamethoxazole (SMX). The mechanism is not known, but may be related to an increase in the volume of distribution of each drug. In addition, trimethoprim can decrease the renal clearance of zidovudine and its glucuronide metabolite by inhibiting their tubular secretion. However, net clearance is not affected because zidovudine is mostly metabolized and the glucuronide may be eliminated via biliary excretion. This interaction is not likely to be clinically important except when hepatic glucuronidation is also impaired by liver disease or drug inhibition.', 'DDInter', 1767317474, 'Recommended management consists of monitoring for side effects of each drug and reducing dosages as necessary.', 'Synergism, Excretion', 'Recommended management consists of monitoring for side effects of each drug and reducing dosages as necessary.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324147, 'Zidovudine', 'Sulindac', 'Moderate', 'Limited clinical data suggest that the concomitant use of zidovudine with nonsteroidal anti-inflammatory agents may increase the risk of hematological toxicity and/or bleeding in HIV-positive patients with hemophilia.', 'DDInter', 1767317474, 'It is recommended to monitor patients for side effects and bleeding during concomitant use.', 'Synergism', 'It is recommended to monitor patients for side effects and bleeding during concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
