INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324184, 'Calcipotriol (topical)', 'Abaloparatide', 'Moderate', 'Elevations in serum calcium levels have been observed in patients treated with topical vitamin D analogs. Concomitant use with systemic vitamin D products, calcium supplements, or other agents that can increase serum calcium concentrations such as thiazide diuretics, estrogens, anabolic steroids, and parathyroid hormone or parathyroid hormone analogs may increase the risk of clinically significant hypercalcemia.', 'DDInter', 1767317474, 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', 'Synergism', 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
