INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324186, 'Digitoxin', 'Abaloparatide', 'Moderate', 'Concomitant use of digitalis preparations with human parathyroid hormone analogs may theoretically potentiate the risk of digitalis toxicity and arrhythmias.', 'DDInter', 1767317474, 'Caution is recommended during concomitant use of digitalis and human parathyroid hormone analogs. Serum calcium and digitalis levels should be closely monitored, and the digitalis dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Synergism', 'Caution is recommended during concomitant use of digitalis and human parathyroid hormone analogs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
