INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1324188, 'Abiraterone', 'Abametapir (topical)', 'Moderate', 'Based on in vitro inhibition data, a single topical application of abametapir lotion may increase plasma concentrations of drugs that are substrates of CYP450 3A4, CYP450 2B6, and CYP450 1A2 isoenzymes and increase their systemic concentrations. The proposed mechanism is high and prolonged systemic exposure to the metabolite abametapir carboxyl, which has been shown to be an in vitro inhibitor of CYP450 3A4, CYP450 2B6, and CYP450 1A2.', 'DDInter', 1767317474, 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion. If abametapir is used, monitor for increased toxicity of the CYP450 3A4, CYP450 2B6, and/or CYP450 1A2 substrate.', 'Metabolism', 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.', NULL, NULL, 'Moderate', 'pharmacodynamic');
