INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1052, 'Abciximab', 'Prasugrel', 'Major', 'The combination of glycoprotein IIb/IIIa (GP IIb/IIIa) inhibitors and other drugs that inhibit platelet aggregation may significantly increase the risk of bleeding, especially from arterial puncture sites. The mechanism is synergistic pharmacologic effects.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations. In addition, invasive procedures, such as urinary catheterization, nasogastric or nasotracheal intubation, and even venous punctures should be avoided or minimized. Non-compressible venous access sites, such as jugular or subclavian sites, should be avoided.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1052/', '[1] Klinkhardt U, Kirchmaier CM, Westrup D, Graff J, Mahnel R, Breddin HK, Harder S "Ex vivo-in vitro interaction between aspirin, clopidogrel, and the glycoprotein IIb/IIIa inhibitors abciximab and SR121566A." Clin Pharmacol Ther 67 (2000): 305-13[2] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.[3] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company, Indianapolis, IN.[4] O''Donoghue M, Antman EM, Braunwald E, et al. "The efficacy and safety of prasugrel with and without a glycoprotein IIb/IIIa inhibitor in patients with acute coronary syndromes undergoing percutaneous intervention: a TRITON-TIMI 38 (Trial to Assess Improvement in Therapeutic Outcomes by Optimizing Pla" J Am Coll Cardiol 54 (2009): 678-85[5] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1053, 'Abciximab', 'Ramucirumab', 'Major', 'Coadministration of ramucirumab and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', NULL, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously.', NULL, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1053/', '[1] "Product Information. Cyramza (ramucirumab)." Eli Lilly and Company, Indianapolis, IN.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1054, 'Abciximab', 'Regorafenib', 'Major', 'Coadministration of regorafenib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with regorafenib is associated with a high frequency of hemorrhage and thrombocytopenia. Coadministration of regorafenib with strong UGT1A9 inhibitors such as mefenamic acid or diflunisal may theoretically increase systemic exposure to regorafenib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1054/', '[1] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[5] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1055, 'Abciximab', 'Reteplase', 'Major', 'The intended and unintended effects of glycoprotein IIb/IIIa inhibitors and thrombolytic agents are additive, such that there is an increased frequency of major bleeding complications, including intracranial hemorrhage, retroperitoneal bleeding, spontaneous gastrointestinal (GI) and genitourinary (GU) bleeding among patients receiving both agents compared with the risk associated with the use of either agent alone.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. The successful use of combination therapy with glycoprotein IIb/IIIa inhibitors and reduced doses of thrombolytics has been reported in clinical trials. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Thrombolytic and/or glycoprotein inhibitor therapy should be discontinued immediately if serious or uncontrollable bleeding occurs.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1055/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[4] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1056, 'Abciximab', 'Rivaroxaban', 'Major', 'Concomitant use of rivaroxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during rivaroxaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis.', NULL, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis.', NULL, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1056/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1057, 'Abciximab', 'Rofecoxib', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents. Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1057/', '[1] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1058, 'Abciximab', 'Salsalate', 'Moderate', 'Although aspirin is routinely given with glycoprotein IIb/IIIa inhibitors, be cognizant of the increased risk of bleeding associated with this combination due to the additive inhibitory effects of salicylates on platelet function.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Therapy should be discontinued immediately if serious or uncontrollable bleeding or thrombocytopenia occurs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1058/', '[1] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001): 1S-2S[2] Klinkhardt U, Kirchmaier CM, Westrup D, Graff J, Mahnel R, Breddin HK, Harder S "Ex vivo-in vitro interaction between aspirin, clopidogrel, and the glycoprotein IIb/IIIa inhibitors abciximab and SR121566A." Clin Pharmacol Ther 67 (2000): 305-13[3] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.[4] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company, Indianapolis, IN.', 1767365203);
