INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1287, 'Abemaciclib', 'Voxelotor', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of abemaciclib and its pharmacologically active metabolites, all of which are substrates of the isoenzyme.', NULL, 'Caution is advised when abemaciclib is used with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised when abemaciclib is used with moderate CYP450 3A4 inhibitors. Dosage adjustments for abemaciclib in 50 mg decrements may be necessary and should be done in accordance with the product labeling. Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, venous thromboembolism, hepatotoxicity, anemia, neutropenia, and thrombocytopenia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1287/', '[1] "Product Information. Verzenio (abemaciclib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Verzenio (abemaciclib)." Lilly, Eli and Company, Indianapolis, IN.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1288, 'Abemaciclib', 'Yellow Fever Vaccine', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1288/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1289, 'Acalabrutinib', 'Abiraterone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when acalabrutinib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when acalabrutinib is used with CYP450 3A4 inhibitors. Patients should be monitored for increased adverse effects such as nausea, vomiting, diarrhea, hemorrhage, infection, cytopenias, and atrial fibrillation or flutter, and the acalabrutinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1289/', '[1] "Product Information. Calquence (acalabrutinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Calquence (acalabrutinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365203);
