INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1291, 'Abiraterone', 'Afatinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of afatinib, which is a substrate of the efflux transporter.', NULL, 'Caution is advised if afatinib is used in combination with P-gp inhibitors.', NULL, 'Caution is advised if afatinib is used in combination with P-gp inhibitors. Patients should be monitored for potentially increased adverse effects such as diarrhea, which may lead to dehydration with or without renal impairment; cutaneous reactions including rash, erythema, and bullous, blistering, or exfoliating lesions; interstitial lung disease such as lung infiltration, pneumonitis, acute respiratory distress syndrome, and allergic alveolitis; hepatotoxicity, which may be life-threatening or fatal; keratitis characterized by acute or worsening eye inflammation, lacrimation, light sensitivity, blurred vision, eye pain, red eye, and/or ulceration; and left ventricular dysfunction. The manufacturer recommends reducing the daily dose of afatinib by 10 mg if not tolerated. The previous dose may be resumed after discontinuation of the P-gp inhibitor as tolerated.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1291/', '[1] "Product Information. Gilotrif (afatinib)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Gilotrif (afatinib)." Boehringer Ingelheim, Ridgefield, CT.[3] "Product Information. Gilotrif (afatinib)." Boehringer Ingelheim, Ridgefield, CT.[4] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1292, 'Abiraterone', 'Salbutamol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1292/', '[1] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Whyte KF, Addis GJ, Whitesmith R, Reid JL "The mechanism of salbutamol-induced hypokalaemia." Br J Clin Pharmacol 23 (1987): 65-71[3] Sanders JP, Potter DE, Ellis S, Bee DE, Grant JA "Metabolic and cardiovascular effects of carbuterol and metaproterenol." J Allergy Clin Immunol 60 (1977): 174-9[4]