INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1318, 'Abiraterone', 'Binimetinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp), uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7, and/or breast cancer resistance protein (BCRP) may increase the plasma levels and risk of adverse effects of binimetinib. The proposed mechanism involves the reduced metabolic clearance of binimetinib through inhibition of P-gp, UGT 1A1, UGT 2B7, and/or BCRP.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors. Binimetinib should be monitored more closely whenever a P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitor is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1318/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Mektovi (binimetinib)." Array BioPharma Inc., Boulder, CO.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1319, 'Abiraterone', 'Bisacodyl', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1319/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767365203);
