INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1328, 'Abiraterone', 'Calaspargase pegol', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1328/', '[1] "Product Information. Oncaspar (pegaspargase)." Rhone-Poulenc Rorer, Collegeville, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bodmer M, Sulz M, Stadlmann S, Droll A, Terracciano L, Krahenbuhl S "Fatal liver failure in an adult patient with acute lymphoblastic leukemia following treatment with L-asparaginase." Digestion 74 (2006): epub[4] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[5] "Product Information. Elspar (asparaginase)." Merck & Co, Inc, West Point, PA.[6] Christ TN, Stock W, Knoebel RW "Incidence of asparaginase-related hepatotoxicity, pancreatitis, and thrombotic events in adults with acute lymphoblastic leukemia treated with a pediatric-inspired regimen." J Oncol Pharm Pract 24 (2018): 299-308[7] Lu G, Karur V, Herrington JD, Walker MG "Successful treatment of pegaspargase-induced acute hepatotoxicity with vitamin B complex and L-carnitine" Proc (Bayl Univ Med Cent) 29 (2016): 46-7[8] Jenkins R, Perlin E "Severe hepatotoxicity from Escherichia coli L-asparaginase." J Natl Med Assoc 79 (1987): 775-9[9] "Product Information. Asparlas (calaspargase pegol)." Servier, Boston, MA.[10] Burke PW, Aldoss I, Lunning MA, et.al "High-grade PEGylated asparaginase-related hepatotoxicity occurrence in a pediatric-inspired adult acute lymphoblastic leukemia regimen does not necessarily predict recurrent hepatotoxicity in subsequent cycles." Blood 122 (2013): 2671[11] "Product Information. Erwinaze (asparaginase Erwinia chrysanthemi)." EUSA Pharma, Newtown, PA.[12] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[13] Al-Nawakil C, Willems L, Mauprivez C, et.al "Successful treatment of l-asparaginase-induced severe acute hepatotoxicity using mitochondrial cofactors." Leuk Lymphoma 55 (2014): 1670-4', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1329, 'Abiraterone', 'Cannabidiol', 'Moderate', 'Coadministration of cannabidiol with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Cannabidiol causes dose-related elevations of liver transaminases, both alanine aminotransferase (ALT) and aspartate aminotransferase (AST).', NULL, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa.', NULL, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa. Serum transaminases and total bilirubin levels should be obtained prior to initiating cannabidiol, and patients with elevated baseline transaminase levels above 3 times the ULN accompanied by elevations in bilirubin above 2 times the ULN should be evaluated. Patients who develop clinical signs or symptoms suggestive of hepatic dysfunction (e.g., unexplained nausea, vomiting, right upper quadrant abdominal pain, fatigue, anorexia, jaundice, dark urine) should have serum transaminases and total bilirubin measured promptly, and cannabidiol treatment interrupted or discontinued as appropriate. Cannabidiol should be discontinued in patients with elevations of transaminase levels greater than 3 times the ULN and bilirubin levels greater than 2 times the ULN. Patients with sustained transaminase elevations of greater than 5 times the ULN should also have treatment discontinued. Patients with prolonged elevations of serum transaminases should be evaluated for other possible causes. Also consider dosage adjustment or discontinuation of any coadministered medication that is known to affect the liver.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1329/', '[1] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1330, 'Abiraterone', 'Capromab pendetide', 'Moderate', 'The effect of surgical and/or medical androgen ablation on the imaging performance of Indium In 111 capromab pendetide has not been evaluated. Preliminary data suggest that hormone ablation may increase expression of Prostate Specific Membrane Antigen (PSMA)--the target of capromab pendetide--and decrease tumor expression of prostate specific antigen (PSA).', NULL, 'Until more data are available, concomitant use of Indium In 111 capromab pendetide in patients treated with androgen ablation therapy is not recommended.', NULL, 'Until more data are available, concomitant use of Indium In 111 capromab pendetide in patients treated with androgen ablation therapy is not recommended.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1330/', '[1] "Product Information. ProstaScint (capromab pendetide)." Aytu BioScience, Inc, Englewood, CO.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1331, 'Abiraterone', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of abiraterone, which is partially metabolized by the isoenzyme.', NULL, 'Concomitant use of abiraterone acetate with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of abiraterone acetate with potent CYP450 3A4 inducers should generally be avoided. Otherwise, an increase in dosing frequency for abiraterone acetate to twice daily during the coadministration period (e.g., from 1000 mg once daily to 1000 mg twice daily) may be considered, although this dosage adjustment has not been studied clinically. The dosage should be adjusted back to the previous level when the CYP450 3A4 inducer is discontinued.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1331/', '[1] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.[2] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[3] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[4] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.[5] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[8] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365203);
