INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (1376, 'Abiraterone', 'Doxepin', 'Moderate', 'Coadministration with abiraterone acetate may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by abiraterone.', NULL, 'Caution is advised if abiraterone acetate must be used concurrently with drugs that undergo metabolism by CYP450 2D6.', NULL, 'Caution is advised if abiraterone acetate must be used concurrently with drugs that undergo metabolism by CYP450 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever abiraterone acetate is added to or withdrawn from therapy. Concomitant use of abiraterone acetate and substrates of CYP450 2D6 with a narrow therapeutic index (e.g., antiarrhythmics) should be avoided if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1376/', '[1] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Zytiga (abiraterone)." Centocor Inc, Malvern, PA.', 1767365203);
