        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1324599, 'Etanercept', 'Abatacept', 'Major', 'The use of abatacept in combination with a tumor necrosis factor (TNF) blocker may increase the risk of infections, possibly due to additive immunosuppressive effects.', NULL, 'Concomitant use of abatacept and TNF blockers is not recommended.', NULL, 'Concomitant use of abatacept and TNF blockers is not recommended. When transitioning from TNF antagonist therapy to abatacept therapy, patients should be monitored closely for signs of infection such as fever, chills, or sore throat.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317474);
