        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1324649, 'Abciximab', 'Trastuzumab emtansine', 'Major', 'Coadministration of trastuzumab emtansine with anticoagulant or antiplatelet drugs may potentiate the risk of hemorrhagic events associated with these drugs. In clinical trials, fatal cases of hemorrhage occurred in patients receiving trastuzumab emtansine in addition to anticoagulant and antiplatelet therapy.', NULL, 'Caution is recommended and additional monitoring should be considered if trastuzumab emtansine must be used with anticoagulant or antiplatelet drugs.', NULL, 'Caution is recommended and additional monitoring should be considered if trastuzumab emtansine must be used with anticoagulant or antiplatelet drugs. In general, monitoring of platelet counts is recommended prior to each dose of trastuzumab emtansine.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317474);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
