    ) VALUES (1324783, 'Aminoglutethimide', 'Abemaciclib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of abemaciclib and its pharmacologically active metabolites, all of which are substrates of the isoenzyme.', NULL, 'Concomitant use of abemaciclib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of abemaciclib with moderate CYP450 3A4 inducers should generally be avoided. Alternative agents without CYP450 3A4 induction potential should be considered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317474);
