        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1324888, 'Abemaciclib', 'Metformin', 'Moderate', 'Coadministration with abemaciclib may increase the plasma concentrations of metformin. The proposed mechanism is abemaciclib inhibition of OCT 2, MATE 1 and MATE 2-K, the renal transporters involved in the active tubular secretion of metformin.', NULL, 'Caution is advised when metformin is used with abemaciclib.', NULL, 'Caution is advised when metformin is used with abemaciclib. Dose adjustments of metformin as well as clinical and laboratory monitoring of glycemic control may be appropriate whenever abemaciclib is added to or withdrawn from therapy. Patients should be advised to be alert to potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, nausea, hunger, weakness, perspiration, palpitations, and tachycardia.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317474);
